def predict_last_value(train_df, test_df, target_col, date_col, **params):
    import pandas as pd
    # Use last known production value as prediction
    last_value = train_df[target_col].iloc[0] if len(train_df) > 0 else 0
    predictions = [last_value] * len(test_df)
    return pd.DataFrame({'predicted': predictions}, index=test_df.index)
