def predict_lagged_mean(train_df, test_df, target_col, **params):
    import pandas as pd
    import numpy as np

    # Use lag_1 as baseline
    baseline_value = train_df['lag_1'].mean()

    predictions = pd.DataFrame({
        'predicted': [baseline_value] * len(test_df)
    }, index=test_df.index)

    return predictions
