def predict_naive_last_value(train_df, test_df, target_col, date_col, **params):
    import pandas as pd
    # Use last production column as prediction
    last_prod_col = train_df.filter(regex=^Production-\d{4}-\d{2}$).columns[-1]
    predictions = train_df[last_prod_col].values
    return pd.DataFrame({\'predicted\': predictions}, index=test_df.index)
